import axios from "axios";
import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import Navbar from "../Component/Navbar";
import HashLoader from "react-spinners/HashLoader";



const OyuncuDetay = () => {
    let { id } = useParams();
    const [oyuncu, setOyuncu] = useState(null)

    const api_link = `https://cryptic-gorge-72989.herokuapp.com/players/${id}`
    axios.get(api_link)
        .then(response => {
            setOyuncu(response.data)
        }, [id])

    if (oyuncu === null) {
        return (
            <div className="aci">
                <h1><HashLoader color="red" /></h1>
            </div>
        )
    }


    return (
        <div>
            <Navbar />
            <div>
                <div className="Profil-cercevesi">
                    <div className="Profil-cerceve-ici">
                        <h4 style={{ textAlign: "center", fontFamily: "-moz-initial" }}>Oyuncu Bilgisi</h4><br />
                        <h5>İsmi: {oyuncu.name}<br /></h5>
                        <h5>Oynadıgı Mevki: {oyuncu.position}<br /></h5>
                        <h5>Yaşı: {oyuncu.age}<br /></h5>
                        <h5>Forma Numarası: {oyuncu.number}<br /></h5>
                        <h5>Oynadıgı Takım: {oyuncu.team.name}</h5>

                    </div>
                    <Link to={"/TakımDetay"}>
                        <button style={{margin:5}} className="btn btn-dark"><i class="fa-solid fa-arrow-left fa-beat-fade fa-2xs"></i></button>
                    </Link>
                </div>



            </div>
        </div>
    )
}

export default OyuncuDetay;
