import axios from "axios";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import Navbar from "../Component/Navbar";
import HashLoader from "react-spinners/HashLoader";


const TakımDetay = () => {

    const [toyuncu, setToyuncu] = useState([])

    const api_link = "https://cryptic-gorge-72989.herokuapp.com/teams"
    axios.get(api_link)
        .then(response => {
            setToyuncu(response.data)
        },[])
        if(toyuncu===null){
            return(
                <div className="aci">
            <h1><HashLoader color="red" /></h1>
            </div>
            )
        }
    return (
        <div>
        <Navbar/>
            <h4 style={{textAlign:"center",fontSize:25,fontFamily:"-moz-initial"}}>Hangi Takımın Futbolcularını Görmek İstiyorsunuz.</h4>

        <div style={{margin:40}} className="col-12 row row-cols-6 row-cols-md-5 g-3">
           {
            toyuncu.map(takımlar=>
                <div>

                   <Link style={{color:"black",textDecoration:"none"}} to={`/${takımlar.id}/Oyuncular`}>
                   <p>{takımlar.name}</p>

                   </Link> 
                </div>
                )
           }

        </div>
        <Link to={"/"}>
        <button style={{margin:5}} className="btn btn-outline-dark"><i class="fa-solid fa-arrow-left fa-beat-fade fa-2xs"></i></button>
        </Link>
        </div>

    )
}

export default TakımDetay;
