import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const OyuncuDetay = () =>{
    let {id} =useParams();
    const [oyuncu,setOyuncu]=useState()

    useEffect(()=>{
        const api_link=`https://cryptic-gorge-72989.herokuapp.com/players/${id}`
        fetch(api_link)
        .then(oyuncu=>oyuncu.json())
        .then(oyuncu=>{
            setOyuncu(oyuncu)
        })
    },[id])

    return(
        <div>
            {
                oyuncu.map(oyuncunun=>
                    <div>
                        {oyuncunun.name}
                    </div>
                    )
            }

        </div>
    )
}

export default OyuncuDetay;
