import axios from "axios";
import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import Navbar from "../Component/Navbar";

const Oyuncular = () => {
    let {id} = useParams();
    const [takım, setTakım] = useState([])


    const api_cek =`https://cryptic-gorge-72989.herokuapp.com/teams/${id}`
    axios.get(api_cek)
        .then(response => {
            setTakım(response.data)
        }, [id])


    return (

        <div>
        <Navbar/>
        <h4 style={{textAlign:"center", fontFamily:"-moz-initial",fontSize:25}}>Takımındaki Oyuncular</h4>
        <div style={{margin:40}} className="col-12 row row-cols-6 row-cols-md-5 g-3">
            {
                takım.players.map(oyuncu=>
                    <Link style={{color:"black",textDecoration:"none"}} to={""}><div>
                        {oyuncu.team.name}
                        {oyuncu.name}
                        {oyuncu.number}


                    </div>
                    </Link>
                    )
            }
        </div>
       <Link to={"/TakımDetay"}>
       <button style={{margin:5}} className="btn btn-dark"><i class="fa-solid fa-arrow-left fa-beat-fade fa-2xs"></i></button>
       </Link>
        </div>
    )
}

export default Oyuncular;
