import axios from "axios";
import React, { useState } from "react";
import { Link } from "react-router-dom";

const TakımDetay = () => {

    const [toyuncu, setToyuncu] = useState([])

    const api_link = "https://cryptic-gorge-72989.herokuapp.com/teams"
    axios.get(api_link)
        .then(response => {
            setToyuncu(response.data)
        })
    return (
        <div>
            <h4 style={{textAlign:"center"}}>Hangi Takımın Futbolcularını Görmek İstiyorsunuz.</h4>
           {
            toyuncu.map(takımlar=>
                <div className="col-11 row row-cols-1 row-cols-md-6 g-1">

                   <Link to={`/${takımlar.id}/Oyuncular`}>
                   <p>{takımlar.name}</p>
                   </Link> 
                </div>
                )
           }
        </div>
    )
}

export default TakımDetay;
